import React, { useEffect, useState } from 'react';
import '@fontsource/work-sans';

interface MonthlyAmountInterface {
  months: number;
  amount: number;
  finalDate: any;
}

export const MONTHLY_AMOUNT = ({
  months,
  amount,
  finalDate,
}: MonthlyAmountInterface) => {
  const [monthlyAmount, setMonthlyAmount] = useState<number>(0);
  useEffect(() => {
    if (months < 1) return;
    let amountNumber = amount.toString().replaceAll(',', '');
    console.log({ amountNumber });

    const tempAmount = Math.floor(amount / months);
    setMonthlyAmount(tempAmount);
  }, [months, amount]);

  return (
    <div
      style={{
        height: '30%',
        border: '1px solid lightgrey',
        borderRadius: 8,
        marginTop: 15,
      }}
    >
      <div
        style={{
          height: '50%',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-around',
        }}
      >
        <span
          style={{
            fontFamily: 'Work Sans',
            fontStyle: 'normal',
            // width: '60%',
            fontWeight: 400,
            fontSize: 20,
            lineHeight: '120%',
            color: '#1E2A32',
          }}
        >
          Monthly Amount
        </span>
        <span
          style={{
            fontFamily: 'Rubik',
            fontStyle: 'normal',
            fontWeight: 500,
            color: '#0079FF',
            fontSize: 32,
            lineHeight: '120%',
          }}
        >
          ${monthlyAmount}
        </span>
      </div>
      <div
        style={{
          height: '50%',
          backgroundColor: '#E5E5E5',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-evenly',
          alignItems: 'center',
          padding: '0px 5px',
        }}
      >
        <span>
          You&apos;re planning <b>{months} monthly deposit</b> to reach your{' '}
          <b> ${amount}</b> goal by {finalDate}
        </span>
      </div>
    </div>
  );
};
