import React, { ReactElement, useState } from 'react';
import DollarIcon from '../../assets/icons/DollarIcon.png';

export const TOTAL_AMOUNT = (props: any): ReactElement => {
  const { amount, setAmount } = props;
  const handleChange = (e: any) => {
    console.log(e.target.value);

    const ff = /^\d*\.?\d*$/.test(e.target.value.replaceAll(',', ''));
    console.log(ff);
    if (!ff) return;

    setAmount(e.target.value);
  };
  const handleBlur = () => {
    console.log(amount);
  };
  const currencyFormatter = new Intl.NumberFormat('en-US');

  const getAmount = () => {
    if (!amount) return amount;
    const foo = currencyFormatter.format(
      Number.parseFloat(amount.replace(/[^0-9.]/g, ''))
    );
    console.log(foo);
    // setAmountToShow(foo);
    return foo;
  };
  return (
    <div
      style={{
        marginRight: 10,
      }}
    >
      <label
        style={{ fontSize: 14, lineHeight: '150%', height: 21 }}
        htmlFor="amount"
      >
        Total Amount{' '}
      </label>
      <br />
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-around',
          alignItems: 'center',
          border: '1px solid #E9EEF2',
          borderRadius: 4,
        }}
      >
        <span>
          <img
            src={DollarIcon}
            alt="Dollar Icon"
            style={{
              width: '8px',
              height: '14px',
            }}
          />
        </span>
        <input
          type="text"
          pattern="([0-9]+.{0,1}[0-9]*,{0,1})*[0-9]"
          id="amount"
          value={getAmount()}
          onChange={(e) => handleChange(e)}
          onBlur={() => handleBlur()}
          style={{
            width: '100%',
            height: 56,
            border: '1px solid #fff',
            fontFamily: 'Rubik',
            fontWeight: 500,
            fontSize: 24,
          }}
        />
      </div>
    </div>
  );
};
