import React, { ReactElement, useState } from 'react';

export const TOTAL_AMOUNT = (): ReactElement => {
  const [amount, setAmount] = useState<number>(0);
  const handleChange = (e: any) => {
    console.log(e.target.value);
    setAmount(e.target.value);
  };
  const handleBlur = () => {
    console.log(amount);
  };
  return (
    <div>
      <label style={{ fontSize: 14, lineHeight: '150%' }} htmlFor="amount">
        Total Amount:{' '}
      </label>
      <input
        id="amount"
        type={'number'}
        value={amount}
        onChange={(e) => handleChange(e)}
        onBlur={() => handleBlur()}
      ></input>
    </div>
  );
};
