import React, { useEffect, useState } from 'react';

interface monthlyAmountInterface {
  months: number;
  amount: number;
  FinalDate: string;
}

export const MONTHLY_AMOUNT = ({
  months,
  amount,
  FinalDate,
}: monthlyAmountInterface) => {
  const [monthlyAmount, setMonthlyAmount] = useState<number>(0);
  useEffect(() => {
    if (months < 1) return;
    let tempAmount = Math.floor(amount / months);
    setMonthlyAmount(tempAmount);
  }, [months, amount]);

  return (
    <div
      style={{
        height: '30%',
      }}
    >
      <div
        style={{
          height: '50%',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-around',
        }}
      >
        <span
          style={{
            fontFamily: 'Work Sans',
            fontStyle: 'normal',
            // width: '60%',
            fontWeight: 400,
            fontSize: 20,
            lineHeight: '120%',
            color: '#1E2A32',
          }}
        >
          Monthly Amount
        </span>
        <span
          style={
            {
              // width: '40%',
            }
          }
        >
          ${monthlyAmount}
        </span>
      </div>
      <div
        style={{
          height: '50%',
          backgroundColor: '#E5E5E5',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-evenly',
          alignItems: 'center',
        }}
      >
        <span>
          You&apos;re planning <b>{months} monthly deposit</b> to reach your{' '}
          <b> ${amount}</b> goal by {FinalDate}
        </span>
      </div>
    </div>
  );
};
