import React, { forwardRef, ReactElement, useState } from 'react';
import DatePicker from 'react-datepicker';

import 'react-datepicker/dist/react-datepicker.css';

// CSS Modules, react-datepicker-cssmodules.css
// import 'react-datepicker/dist/react-datepicker-cssmodules.css';

export const DATE_PICKER = () => {
  const [startDate, setStartDate] = useState(new Date());
  const CUSTOM_INPUT = (props: any): ReactElement => {
    const { onChange, onClick, value } = props;
    const getValue = (): string => {
      var tempDivElement = document.createElement('div');

      // Set the HTML content with the given
      tempDivElement.innerHTML = 'dfdsfds \n\n 2001';
      return tempDivElement.textContent || tempDivElement.innerText || 'dddd';
    };
    return (
      <input
        style={{ height: 40 }}
        onChange={onChange}
        value={getValue()}
        onClick={onClick}
      />
    );
  };
  return (
    <React.Fragment>
      <DatePicker
        selected={startDate}
        onChange={(date) => date && setStartDate(date)}
        dateFormat="MMMM, yyyy"
        showMonthYearPicker
        minDate={new Date()}
        customInput={<CUSTOM_INPUT />}
      />
    </React.Fragment>
  );
};
